_old_init = init
def init(config):
    import os, os.path
    if not config.get('datadir', None):
        try:
            datadir = os.path.join(os.path.expanduser('~'), '.blockstream', 'gdk')
            os.makedirs(os.path.join(datadir, 'assets'), exist_ok = True);
            config['datadir'] = datadir
        except:
          pass
    return _old_init(json.dumps(config))
